effect.vertex:


      #version 330 core

			layout (location = 0) in vec3	in_position;

      uniform vec4				effect_diffuse;
      uniform vec4        effect_specular;
      uniform mat4        effect_t_world;
      uniform mat4        effect_t_view;
      uniform mat4        effect_t_proj;

			out vec4						var_color;

			void main () {

        //mat4 mvp = effect_t_proj;

        var_color = effect_diffuse;
				//gl_Position = mvp * vec4 (in_position.xyz, 1.0);
        gl_Position = vec4 (in_position.xyz, 1.0);
			}

effect.fragment:


#version 330 core

in vec4 var_color;
out vec4 out_color;

void main () {
  out_color = var_color;
}
